import React, { useState } from "react";
import {
  AppstoreOutlined,
  MailOutlined,
  SettingOutlined,
  UserOutlined,
  UnorderedListOutlined,
  BookOutlined,
} from "@ant-design/icons";
import type { MenuProps } from "antd";
import { Menu, Layout, Avatar } from "antd";

type MenuItem = Required<MenuProps>["items"][number];

const rootSubmenuKeys = ["sub1", "sub2", "sub4"];

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}
const items = [
  getItem("Home", "sub1", <AppstoreOutlined />),
  getItem("Category", "sub2", <UnorderedListOutlined />),
  getItem("Author", "sub3", <UserOutlined />),
  getItem("Book", "sub4", <BookOutlined />),
  getItem("User", "sub5", <UserOutlined />, [
    getItem("Option 1", "1"),
    getItem("Option 2", "2"),
    getItem("Option 3", "3"),
    getItem("Option 4", "4"),
  ]),
  // getItem("Navigation Two", "sub3", <AppstoreOutlined />, [
  //   getItem("Option 5", "5"),
