        <Layout>
          <Header style={{ padding: 0 }}>
            <div style={{ position: "absolute", right: 16 }}>
              <Avatar size={40} icon={<UserOutlined />} />
            </div>
          </Header>
          <Content style={{ margin: "24px 16px 0" }}>
            <div
