import React, { useEffect, useState } from "react";
import { UserOutlined } from "@ant-design/icons";
import {
  Layout,
  Menu,
  theme,
  Breadcrumb,
  Button,
  Avatar,
  Dropdown,
  Space,
  Form,
} from "antd";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";
import Content from "../../components/admin/Content";

const { Header, Sider } = Layout;

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const items = [
  {
    label: <button style={{
      background: "none",
      border: "none",
      padding: 0,
      cursor: "pointer",
    }} href="#/">Edit Profile</button>,
    key: "0",
  },
  {
    label: <a href="#/">Change Password</a>,
    key: "1",
  },
];

const Dashboard = () => {
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  const navigate = useNavigate();

  const [userName, setUserName] = useState("");
  const [id, setId] = useState("");
  const [isChangePasswordVisible, setChangePasswordVisible] = useState(false);
  const [changePasswordForm] = Form.useForm();

  useEffect(() => {
    const token = localStorage.getItem("token");

    // const parsedToken = JSON.parse(token);
    // const isAdmin = token && token.includes("ROLE_ADMIN");
    // const storedUserName = JSON.parse(token).userName;

    const isAdmin = JSON.parse(token).roleNames.includes("ROLE_ADMIN");

    if (!isAdmin) {
      navigate("/admin/login");
    } else {
      setUserName(JSON.parse(token).userName);
      setId(JSON.parse(token).id);
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/admin/login");
  };

  const handlePasswordChange = () => {
    changePasswordForm
      .validateFields()
      .then((values) => {
        // Make the API call or perform the database update here
        const { newPassword } = values;
        // Replace `localhost:8080/api/backend/user/edit/${id}` with your actual API endpoint
        fetch(`localhost:8080/api/backend/user/edit/${id}`, {
          method: "POST",
          body: JSON.stringify({ newPassword }),
          headers: {
            "Content-Type": "application/json",
          },
        })
          .then((response) => {
            if (response.ok) {
              message.success("Password changed successfully");
              changePasswordForm.resetFields();
              setChangePasswordVisible(false);
            } else {
              message.error("Failed to change password");
            }
          })
          .catch((error) => {
            console.log("API error:", error);
            message.error("An error occurred");
          });
      })
      .catch((error) => {
        console.log("Validation failed:", error);
      });
  };

  return (
    <Layout>
      <Header
        style={{
          display: "flex",
          alignItems: "center",
          position: "sticky",
          top: 0,
          zIndex: 1,
          width: "100%",
        }}
      >
        <div className="demo-logo" />
        <div
          style={{ marginLeft: "auto", display: "flex", alignItems: "center" }}
        >
          <Dropdown
            menu={{
              items,
            }}
            trigger={["click"]}
          >
            <button
              style={{
                background: "none",
                border: "none",
                padding: 0,
                cursor: "pointer",
              }}
              onClick={(e) => e.preventDefault()}
