import React, { useEffect, useState } from "react";
import {
  UserOutlined,
} from "@ant-design/icons";
import { Layout, Menu, theme, Breadcrumb, Button, Avatar } from "antd";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

const { Header, Content, Sider } = Layout;

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const Dashboard = () => {
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  const navigate = useNavigate();

  const [userName, setUserName] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");

    // const parsedToken = JSON.parse(token);
    // const isAdmin = token && token.includes("ROLE_ADMIN");
    // const storedUserName = JSON.parse(token).userName;

    const isAdmin = JSON.parse(token).roleNames.includes("ROLE_ADMIN");

    if (!isAdmin) {
      navigate("/admin/login");
    } else {
      setUserName(JSON.parse(token).userName);
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/admin/login");
  };

  return (
    <Layout>
      <Header
        style={{
          display: "flex",
          alignItems: "center",
          position: "sticky",
          top: 0,
          zIndex: 1,
          width: "100%",
        }}
      >
        <div className="demo-logo" />
        <div
          style={{ marginLeft: "auto", display: "flex", alignItems: "center" }}
        >
          {/* <Avatar
            style={{
              backgroundColor: "#87d068",
              marginRight: 10,
            }}
            icon={<UserOutlined />}
          />
          <span style={{ color: "#fff", marginRight: 20 }}>{userName}</span> */}
          <Button type="text" onClick={handleLogout} style={{ color: "#fff", display: "flex", alignItems: "center", marginLeft: "auto" }}>
            <span class="material-symbols-outlined">logout</span>
          </Button>
        </div>
      </Header>
      <Layout>
        <Sider
          width={250}
          style={{
            background: colorBgContainer,
            height: "100vh",
          }}
        >
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{
              height: "100%",
              borderRight: 0,
            }}
            items={[
              getItem(
                <a
                  href="/admin/dashboard"
                  style={{ display: "flex", alignItems: "center" }}
                >
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    dashboard
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Dashboard
                  </p>
                </a>,
                "sub1"
              ),
              {
                type: "divider",
              },
              getItem(
                <a href="#/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    book
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>Books</p>
                </a>,
                "sub2"
              ),
              getItem(
                <a href="#/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    contact_page
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>Author</p>
                </a>,
                "sub3"
              ),
              getItem(
                <a href="#/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    category
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Category
                  </p>
                </a>,
                "sub4"
              ),
              getItem(
                <a href="#/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    folder_shared
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>Users</p>
                </a>,
                "sub5",
                null,
                [
                  getItem(
                    <a
                      href="#/"
                      style={{ display: "flex", alignItems: "center" }}
                    >
                      <span
                        class="material-symbols-outlined"
                        style={{ marginRight: 10 }}
                      >
                        assignment_ind
                      </span>
                      <p style={{ fontSize: "16px", fontWeight: "300" }}>
                        User Profile
                      </p>
                    </a>,
                    "6"
                  ),
                  getItem(
                    <a
                      href="#/"
                      style={{ display: "flex", alignItems: "center" }}
                    >
                      <span
                        class="material-symbols-outlined"
                        style={{ marginRight: 10 }}
                      >
                        edit
                      </span>
                      <p style={{ fontSize: "16px", fontWeight: "300" }}>
                        User Edit
                      </p>
                    </a>,
                    "7"
                  ),
                  getItem(
                    <a
                      href="#/"
                      style={{ display: "flex", alignItems: "center" }}
                    >
                      <span
                        class="material-symbols-outlined"
                        style={{ marginRight: 10 }}
                      >
                        person_add
                      </span>
                      <p style={{ fontSize: "16px", fontWeight: "300" }}>
                        User Add
                      </p>
                    </a>,
                    "8"
                  ),
                ]
              ),
              getItem(
                <a
                  href="#/"
                  style={{ display: "flex", alignItems: "center" }}
                >
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    shopping_cart
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Invoice
                  </p>
                </a>,
                "sub6"
              ),
              getItem(
                <a
                  href="#/"
                  style={{ display: "flex", alignItems: "center" }}
                >
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    settings
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Setting
                  </p>
                </a>,
                "sub5"
              ),
            ]}
          />
        </Sider>
        <Layout
          style={{
            padding: "0 24px 24px",
          }}
        >
          <Breadcrumb
            style={{
              margin: "16px 0",
            }}
            items={[
              {
                title: "Admin",
              },
              {
                title: <a href="/admin/dashboard">Dashboard</a>,
              },
            ]}
          />
          <Content
            style={{
              padding: 24,
              margin: 0,
              minHeight: 280,
              background: colorBgContainer,
            }}
          >
            Content
          </Content>
        </Layout>
      </Layout>
    </Layout>
  );
};

export default Dashboard;
