import React from "react";
import { Button, Checkbox, Form, Input, Row, Col, notification } from "antd";
import axios from "axios";
import { useNavigate } from "react-router-dom";
// import { RadiusUprightOutlined } from "@ant-design/icons";

export default function LoginForm({ onFinish, onFinishFailed }) {
  const navigate = useNavigate();
  const [api, contextHolder] = notification.useNotification();

  const openErrorNotification = (type, message, description) => {
    api[type]({
      message: message,
      description: description,
    });
  };

  const handleFinish = (values) => {
    console.log("Success:", values);
    axios
      .post("http://localhost:8080/api/backend/user/login", values)
      .then((res) => {
        if (res.data.message === "OK") {
          const isAdmin = res.data.data.roleNames.findIndex(
            (role) => role === "ROLE_ADMIN"
          );
          if (isAdmin === -1) {
            openErrorNotification(
              "warning",
              "Login Failed",
              "You are not admin."
            );
            return;
          } else {
            localStorage.setItem("token", res.data.data);
            navigate("/admin/dashboard");
            onFinish();
          }
        } else {
          openErrorNotification(
            "error",
            "Login Failed",
            "Invalid email or password. Please try again."
          );
        }
      })
      .catch((error) => {
        openErrorNotification(
          "error",
          "Login Failed",
          "Invalid email or password. Please try again."
        );
      });
  };

  return (
    <>
      {contextHolder}
      <Row>
        <Col span={8}>
        <Form
        name="basic"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
        style={{ maxWidth: 600 }}
        initialValues={{ remember: true }}
        onFinishFailed={onFinishFailed}
        autoComplete="off"
        onFinish={handleFinish}
      >
        <Form.Item
          label="Email"
          name="email"
          rules={[{ required: true, message: "Please input your email!" }]}
          className="login-form-label"
        >
          <Input size="large" />
        </Form.Item>

        <Form.Item
          label="Password"
          name="password"
          rules={[{ required: true, message: "Please input your password!" }]}
          className="login-form-label"
        >
          <Input.Password size="large" />
        </Form.Item>

        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
          <Row justify="space-between" align="middle">
            <Col>
              <Form.Item name="remember" valuePropName="checked" noStyle>
                <Checkbox>Remember me</Checkbox>
              </Form.Item>
            </Col>
            <Col>
              <a
                href="https://google.com"
                target="_blank"
                rel="noopener noreferrer"
              >
                Forget password
              </a>
            </Col>
          </Row>
        </Form.Item>

        <Form.Item >
          <Button
            type="primary"
            htmlType="submit"
            className="login-form-submit"
            size="large"
          >
            Login
          </Button>
        </Form.Item>
      </Form>
        </Col>
      </Row>

    </>
  );
}
