import React, { useEffect, useState } from "react";
import { UserOutlined } from "@ant-design/icons";
import {
  Layout,
  Menu,
  theme,
  Breadcrumb,
  Button,
  Avatar,
  Dropdown,
  Space,
  Form,
  Modal,
  Input,
} from "antd";
import { useNavigate, Link } from "react-router-dom";
import "./Dashboard.css";
import Content from "../../components/admin/Content";

const { Header, Sider } = Layout;

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const Dashboard = () => {
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  const navigate = useNavigate();

  const [userName, setUserName] = useState("");
  const [id, setId] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");

    // const parsedToken = JSON.parse(token);
    // const isAdmin = token && token.includes("ROLE_ADMIN");
    // const storedUserName = JSON.parse(token).userName;

    const isAdmin = JSON.parse(token).roleNames.includes("ROLE_ADMIN");

    if (!isAdmin) {
      navigate("/admin/login");
    } else {
      setUserName(JSON.parse(token).userName);
      setId(JSON.parse(token).id);
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/admin/login");
  };

  return (
    <Layout>
      <Header
        style={{
          display: "flex",
          alignItems: "center",
          position: "sticky",
          top: 0,
          zIndex: 1,
          width: "100%",
        }}
      >
        <div className="demo-logo" />
        <div
          style={{ marginLeft: "auto", display: "flex", alignItems: "center" }}
        >
            <Dropdown
    menu={{
      items,
    }}
    trigger={['click']}
  >
    <a onClick={(e) => e.preventDefault()}>
      <Space>
        Click me
        <DownOutlined />
      </Space>
    </a>
  </Dropdown>
          <Button
            type="text"
            onClick={handleLogout}
            style={{
              color: "#fff",
              display: "flex",
              alignItems: "center",
              marginLeft: "auto",
            }}
          >
            <span className="material-symbols-outlined">logout</span>
          </Button>
        </div>
      </Header>
      <Layout>
        <Sider
          width={250}
          style={{
            background: colorBgContainer,
            height: "100vh",
          }}
        >
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{
              height: "100%",
              borderRight: 0,
            }}
            items={[
              getItem(
                <a
                  href="/admin/dashboard"
                  style={{ display: "flex", alignItems: "center" }}
                >
                  <span
                    className="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    dashboard
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Dashboard
                  </p>
                </a>,
                "sub1"
              ),
              {
                type: "divider",
              },
              getItem(
                <a href="#/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    className="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
