import React, { useEffect, useState } from "react";
import { UserOutlined } from "@ant-design/icons";
import {
  Layout,
  Menu,
  theme,
  Breadcrumb,
  Button,
  Avatar,
  Dropdown,
  Space,
  Form,
  Modal,
  Input,
} from "antd";
import { useNavigate, Link } from "react-router-dom";
import "./Dashboard.css";
import Content from "../../components/admin/Content";

const { Header, Sider } = Layout;

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const Dashboard = () => {
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  const navigate = useNavigate();

  const [userName, setUserName] = useState("");
  const [isChangePassword, setChangePassword] = useState(false);
const [changePasswordForm] = Form.useForm();

  useEffect(() => {
    const token = localStorage.getItem("token");

    // const parsedToken = JSON.parse(token);
    // const isAdmin = token && token.includes("ROLE_ADMIN");
    // const storedUserName = JSON.parse(token).userName;

    const isAdmin = JSON.parse(token).roleNames.includes("ROLE_ADMIN");

    if (!isAdmin) {
      navigate("/admin/login");
    } else {
      setUserName(JSON.parse(token).userName);
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/admin/login");
  };

  const handlePasswordChange = () => {
    changePasswordForm.validateFields().then((values) => {
      // Perform password change logic here
      console.log(values);
      // Reset form fields
      changePasswordForm.resetFields();
      // Close the modal
      setChangePassword(false);
    });
