import React from "react";
import {
  UserOutlined,
  // HomeOutlined,
  UnorderedListOutlined,
  SettingOutlined,
  AppstoreOutlined,
  BookOutlined,
  TeamOutlined,
} from "@ant-design/icons";
import { Layout, Menu, theme, Avatar } from "antd";

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const items = [
  getItem("Home", "sub1", <AppstoreOutlined />),
  getItem("Category", "sub2", <UnorderedListOutlined />),
  getItem("Author", "sub3", <UserOutlined />),
  getItem("Book", "sub4", <BookOutlined />),
  getItem("User", "sub5", <TeamOutlined />, [
    getItem("User Profile", "sub5-1", <UserOutlined />),
  ]),
  getItem("Setting", "sub6", <SettingOutlined />),
];

export default function Dashboard() {
  const { Header, Sider, Content, Footer } = Layout;
  // const {
  //   token: { colorBgContainer },
  // } = theme.useToken();

  return (
    <>
      <Layout>
        <Sider
          breakpoint="lg"
          collapsedWidth="0"
          style={{ height: "100vh", width: "250px" }}
          // onBreakpoint={(broken) => {
          //   console.log(broken);
          // }}
          // onCollapse={(collapsed, type) => {
          //   console.log(collapsed, type);
          // }}
        >
          <div
            className="logo"
            style={{ display: "flex", justifyContent: "center" }}
          >
            <img
              src="https://images.pexels.com/photos/4982297/pexels-photo-4982297.jpeg?auto=compress&cs=tinysrgb&w=800"
              alt="Logo"
              style={{ width: "30px", margin: "10px" }}
            />
          </div>
          <Menu
            theme={"dark"}
            defaultOpenKeys={["sub1"]}
            mode="inline"
            items={items}
            style={{ fontSize: "18px", fontWeight: "300", backgroundColor: "#3e5fce" }}
          />
        </Sider>
        <Layout>
          <Header style={{ padding: 0, background: "#3e5fce" }}>
            <div style={{ position: "absolute", right: 16 }}>
              <Avatar size={40} icon={<UserOutlined />} />
            </div>
          </Header>
          <Content style={{ margin: "24px 16px 0" }}>
            <div
              style={{
                padding: 24,
                minHeight: 360,
                background: "3e5fce",
              }}
            >
              content
            </div>
          </Content>
          <Footer style={{ textAlign: "center" }}>
            Ant Design Â©2023 Created by Ant UED
          </Footer>
        </Layout>
      </Layout>
    </>
  );
}
