import React from "react";
import {
  UserOutlined,
  // HomeOutlined,
  UnorderedListOutlined,
  SettingOutlined,
  AppstoreOutlined,
} from "@ant-design/icons";
import { Layout, Menu, theme, Avatar } from "antd";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHome } from "@fortawesome/free-solid-svg-icons";
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css" integrity="sha512-iecdLmaskl7CVkqkXNQ/ZH/XLlvWZOJyj7Yy7tcenmpD1ypASozpmT/E0iPtmFIB46ZmdtAc9eNBvH0H/ZpiBw==" crossorigin="anonymous" referrerpolicy="no-referrer" />

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const items = [
  getItem("Home", "sub1", <FontAwesomeIcon icon="fa-regular fa-house" />),
  getItem("Category", "sub2", <UnorderedListOutlined />),
  getItem("Setting", "sub4", <SettingOutlined />),
];

export default function Dashboard() {
  const { Header, Sider, Content, Footer } = Layout;
  const {
    token: { colorBgContainer },
  } = theme.useToken();

  return (
    <>
      <Layout>
        <Sider
          breakpoint="lg"
          collapsedWidth="0"
          style={{ height: "100vh" }}
          onBreakpoint={(broken) => {
            console.log(broken);
          }}
          onCollapse={(collapsed, type) => {
            console.log(collapsed, type);
          }}
        >
          <div
            className="logo"
            style={{ display: "flex", justifyContent: "center" }}
          >
            <img
              src="https://images.pexels.com/photos/4982297/pexels-photo-4982297.jpeg?auto=compress&cs=tinysrgb&w=800"
              alt="Logo"
              style={{ width: "30px", margin: "10px" }}
            />
          </div>
          <Menu
            theme={"dark"}
            defaultOpenKeys={["sub1"]}
            mode="inline"
            items={items}
            style={{ fontSize: "18px", fontWeight: "300" }}
          />
        </Sider>
        <Layout>
          <Header style={{ padding: 0, background: colorBgContainer }}>
            <div style={{ position: "absolute", right: 16 }}>
              <Avatar size={40} icon={<UserOutlined />} />
            </div>
          </Header>
          <Content style={{ margin: "24px 16px 0" }}>
            <div
              style={{
                padding: 24,
                minHeight: 360,
                background: colorBgContainer,
              }}
            >
              content
            </div>
          </Content>
          <Footer style={{ textAlign: "center" }}>
            Ant Design Â©2023 Created by Ant UED
          </Footer>
        </Layout>
      </Layout>
    </>
  );
}
