import React from "react";
import { Button, Checkbox, Form, Input, Row, Col, notification } from "antd";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { RadiusUprightOutlined } from "@ant-design/icons";

export default function LoginForm({ onFinish, onFinishFailed }) {
  const navigate = useNavigate();
  const [api, contextHolder] = notification.useNotification();

  const openNotificationWithIcon = (type) => {
    api[type]({
      message: 'Notification Title',
      description:
        'This is the content of the notification. This is the content of the notification. This is the content of the notification.',
    });
  };

  const handleFinish = (values) => {
    console.log("Success:", values);
    axios
      .post("http://localhost:8080/api/backend/user/login", values)
      .then((res) => {
        if (res.data.message === "OK") {
          localStorage.setItem("token", res.data.data);
          navigate("/admin/dashboard");
          onFinish();
        } else {
          // showLoginFailedNotification();
        }
      })
      .catch((error) => {
        showLoginFailedNotification();
      });
  };

  const showLoginFailedNotification = () => {
    notification.open = (type) => ({
      message: "Login Failed",
      description: "Invalid email or password. Please try again.",
      icon: <RadiusUprightOutlined />,
      placement: "topRight",
    });
  };

  return (
    <Form
      name="basic"
      labelCol={{ span: 8 }}
      wrapperCol={{ span: 16 }}
      style={{ maxWidth: 600 }}
      initialValues={{ remember: true }}
      onFinishFailed={onFinishFailed}
      autoComplete="off"
      onFinish={handleFinish}
    >
      <Form.Item
        label="Email"
        name="email"
        rules={[{ required: true, message: "Please input your email!" }]}
        className="login-form-label"
      >
        <Input size="large" />
      </Form.Item>

      <Form.Item
        label="Password"
        name="password"
        rules={[{ required: true, message: "Please input your password!" }]}
        className="login-form-label"
      >
        <Input.Password size="large" />
      </Form.Item>

      <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
        <Row justify="space-between" align="middle">
          <Col>
            <Form.Item name="remember" valuePropName="checked" noStyle>
              <Checkbox>Remember me</Checkbox>
            </Form.Item>
          </Col>
          <Col>
            <a
              href="https://google.com"
              target="_blank"
              rel="noopener noreferrer"
            >
              Forget password
            </a>
          </Col>
        </Row>
      </Form.Item>

      <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
        <Button
          type="primary"
          htmlType="submit"
          className="login-form-submit"
          size="large"
        >
          Login
        </Button>
      </Form.Item>
    </Form>
  );
}
