import React, { useEffect, useState } from "react";
import {
  UserOutlined,
  LogoutOutlined,
  LaptopOutlined,
  NotificationOutlined,
  MailOutlined,
  AppstoreOutlined,
  SettingOutlined,
  BookOutlined,
} from "@ant-design/icons";
import { Layout, Menu, theme, Breadcrumb, Button, Avatar } from "antd";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

const { Header, Content, Footer, Sider } = Layout;

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const Dashboard = () => {
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  const navigate = useNavigate();

  const [userName, setUserName] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");

    // const parsedToken = JSON.parse(token);
    // const isAdmin = token && token.includes("ROLE_ADMIN");
    // const storedUserName = JSON.parse(token).userName;

    const isAdmin = JSON.parse(token).roleNames.includes("ROLE_ADMIN");

    if (!isAdmin) {
      navigate("/admin/login");
    } else {
      setUserName(JSON.parse(token).userName);
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/admin/login");
  };

  return (
    <Layout>
      <Header
        style={{
          display: "flex",
          alignItems: "center",
          position: "sticky",
          top: 0,
          zIndex: 1,
          width: "100%",
        }}
      >
        <div className="demo-logo" />
        <div
          style={{ marginLeft: "auto", display: "flex", alignItems: "center" }}
        >
          <Avatar
            style={{
              backgroundColor: "#87d068",
              marginRight: 10,
            }}
            icon={<UserOutlined />}
          />
          <span style={{ color: "#fff", marginRight: 20 }}>{userName}</span>
          <Button type="text" onClick={handleLogout} style={{ color: "#fff" }}>
            Logout
            <LogoutOutlined style={{ marginLeft: 4, color: "#fff" }} />
          </Button>
        </div>
      </Header>
      <Layout>
        <Sider
          width={250}
          style={{
            background: colorBgContainer,
            height: "100vh",
          }}
        >
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{
              height: "100%",
              borderRight: 0,
            }}
            items={[
              getItem(
                <a
                  href="/admin/dashboard"
                  style={{ display: "flex", alignItems: "center" }}
                >
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    dashboard
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Dashboard
                  </p>
                </a>,
                "sub1"
              ),
              {
                type: "divider",
              },
              getItem(
                <a href="/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    book
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>Book</p>
                </a>,
                "sub2"
              ),
              getItem(
                <a href="/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    contact_page
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>Author</p>
                </a>,
                "sub3"
              ),
              getItem(
                <a href="/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    category
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Category
                  </p>
                </a>,
                "sub4"
              ),
              getItem(
                <a href="/" style={{ display: "flex", alignItems: "center" }}>
                  <span
                    class="material-symbols-outlined"
                    style={{ marginRight: 10 }}
                  >
                    category
                  </span>
                  <p style={{ fontSize: "16px", fontWeight: "300" }}>
                    Category
                  </p>
                </a>,
                "sub5",
                null,
                [
                  getItem("Option 9", "9"),
                  getItem("Option 10", "10"),
                  getItem("Option 11", "11"),
                  getItem("Option 12", "12"),
                ]
              ),
            ]}
          />
        </Sider>
        <Layout
          style={{
            padding: "0 24px 24px",
          }}
        >
          <Breadcrumb
            style={{
              margin: "16px 0",
            }}
            items={[
              {
                title: "Admin",
              },
              {
                title: <a href="/admin/dashboard">Dashboard</a>,
              },
            ]}
          />
          <Content
            style={{
              padding: 24,
              margin: 0,
              minHeight: 280,
              background: colorBgContainer,
            }}
          >
            Content
          </Content>
        </Layout>
      </Layout>
    </Layout>
  );
};

export default Dashboard;
