import React, { useEffect, useState } from "react";
import {
  UserOutlined,
  LogoutOutlined,
  LaptopOutlined,
  NotificationOutlined,
  MailOutlined,
  AppstoreOutlined,
  SettingOutlined,
} from "@ant-design/icons";
import { Layout, Menu, theme, Breadcrumb, Button, Avatar } from "antd";
import { useNavigate, Link } from "react-router-dom";

const { Header, Content, Footer, Sider } = Layout;

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const Dashboard = () => {
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  const navigate = useNavigate();

  const [userName, setUserName] = useState("");
  // const handle = () => {
  //   localStorage.setItem("userName", userName);
  // };

  useEffect(() => {
    const token = localStorage.getItem("token");

    const isAdmin = token && token.includes("ROLE_ADMIN");
    const user = localStorage.getItem("user");

    if (!isAdmin) {
      navigate("/admin/login");
    } 
    else {
      setUserName(localStorage.getItem("userName"));
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/admin/login");
  };

  const items = [
    getItem(
      <a href="/admin/dashboard">Dashboard</a>,
      "sub1",
      <AppstoreOutlined />
    ),
    {
      type: "divider",
    },
    getItem("Navigation Three", "sub2", <SettingOutlined />),
  ];

  return (
    <Layout>
      <Header
        style={{
          display: "flex",
          alignItems: "center",
          position: "sticky",
          top: 0,
          zIndex: 1,
          width: "100%",
        }}
      >
        <div className="demo-logo" />
        <div
          style={{ marginLeft: "auto", display: "flex", alignItems: "center" }}
        >
          <Avatar
            style={{
              backgroundColor: "#87d068",
              marginRight: 10,
            }}
            icon={<UserOutlined />}
          />
          <span style={{ color: "#fff", marginRight: 20 }}>
            {userName}
          </span>
          <Button type="text" onClick={handleLogout} style={{ color: "#fff" }}>
            Logout
            <LogoutOutlined style={{ marginLeft: 4, color: "#fff" }} />
          </Button>
        </div>
      </Header>
      <Layout>
        <Sider
          width={200}
          style={{
            background: colorBgContainer,
            height: "100vh",
          }}
        >
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{
              height: "100%",
              borderRight: 0,
            }}
            items={items}
          />
        </Sider>
        <Layout
          style={{
            padding: "0 24px 24px",
          }}
        >
          <Breadcrumb
            style={{
              margin: "16px 0",
            }}
            items={[
              {
                title: "Admin",
              },
              {
                title: <a href="/admin/dashboard">Dashboard</a>,
              },
            ]}
          />
          <Content
            style={{
              padding: 24,
              margin: 0,
              minHeight: 280,
              background: colorBgContainer,
            }}
          >
            Content
          </Content>
        </Layout>
      </Layout>
    </Layout>
  );
};

export default Dashboard;
