import React, { useState } from "react";
import {
  AppstoreOutlined,
  MailOutlined,
  SettingOutlined,
  UserOutlined} from "@ant-design/icons";
import type { MenuProps} from "antd";
import { Menu, Layout, Avatar  } from "antd";

type MenuItem = Required<MenuProps>["items"][number];

// function getItem(label, key, icon, children, type) {
//   return {
//     key,
//     icon,
//     children,
//     label,
//     type,
//   };
// }

// const items = [
//   getItem("Home", "sub1", <AppstoreOutlined />),
//   getItem("Category", "sub2", <UnorderedListOutlined />),
//   getItem("Author", "sub3", <UserOutlined />),
//   getItem("Book", "sub4", <BookOutlined />),
//   getItem("User", "sub5", <TeamOutlined />, [
//     getItem("User Profile", "sub5-1", <UserOutlined />),
//   ]),
//   getItem("Setting", "sub6", <SettingOutlined />),
// ];

const rootSubmenuKeys = ["sub1", "sub2", "sub4"];

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}
const items = [
  getItem("Navigation One", "sub1", <MailOutlined />, [
    getItem("Option 1", "1"),
    getItem("Option 2", "2"),
    getItem("Option 3", "3"),
    getItem("Option 4", "4"),
  ]),
  getItem("Navigation Two", "sub2", <AppstoreOutlined />, [
    getItem("Option 5", "5"),
    getItem("Option 6", "6"),
    getItem("Submenu", "sub3", null, [
      getItem("Option 7", "7"),
      getItem("Option 8", "8"),
    ]),
  ]),
  getItem("Navigation Three", "sub4", <SettingOutlined />, [
    getItem("Option 9", "9"),
    getItem("Option 10", "10"),
    getItem("Option 11", "11"),
    getItem("Option 12", "12"),
  ]),
];

export default function Dashboard() {
  const { Header, Sider, Content, Footer } = Layout;

  const [openKeys, setOpenKeys] = useState(["sub1"]);
  const onOpenChange = (keys) => {
    const latestOpenKey = keys.find((key) => openKeys.indexOf(key) === -1);
    if (rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
      setOpenKeys(keys);
    } else {
      setOpenKeys(latestOpenKey ? [latestOpenKey] : []);
    }
  };

  return (
    <>
      <Layout>
        <Sider
          breakpoint="lg"
          collapsedWidth="0"
          style={{
            height: "100vh",
            width: "250px",
            backgroundColor: "#3e5fce",
          }}
        >
          <div
            className="logo"
            style={{ display: "flex", justifyContent: "center" }}
          >
            <img
              src="https://images.pexels.com/photos/4982297/pexels-photo-4982297.jpeg?auto=compress&cs=tinysrgb&w=800"
              alt="Logo"
              style={{ width: "30px", margin: "10px" }}
            />
          </div>
          <Menu
            mode="inline"
            openKeys={openKeys}
            onOpenChange={onOpenChange}
            style={{ width: 256 }}
            items={items}
          />
        </Sider>
        <Layout>
          <Header style={{ padding: 0 }}>
            <div style={{ position: "absolute", right: 16 }}>
              <Avatar size={40} icon={<UserOutlined />} />
            </div>
          </Header>
          <Content style={{ margin: "24px 16px 0" }}>
            <div
              style={{
                padding: 24,
                minHeight: 360,
              }}
            >
              content
            </div>
          </Content>
          <Footer style={{ textAlign: "center" }}>
            Ant Design ©2023 Created by Ant UED
          </Footer>
        </Layout>
      </Layout>
    </>
  );
}
